import React, { useState, useEffect, useRef } from "react";

function PodcastPlayer(props) {
  const [playerState, setPlayerState] = useState([]);
  const audioElement = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    fetch(props.rssfeed)
      .then((response) => response.text())
      .then((str) => new window.DOMParser().parseFromString(str, "text/xml"))
      .then((data) => {
        const itemList = data.querySelectorAll("item");
        let html = ``;
        const items = [];
        itemList.forEach((e) => {
          items.push({
            pubDate: new Date(e.querySelector("pubDate").textContent),
            title: e.querySelector("title").textContent,
            sourceAudio: e.querySelector("enclosure").getAttribute("url"),
            description: e.querySelector("description").textContent,
            episodeImage: e.childNodes[21].attributes.href.value,
          });
        });
        setPlayerState(items);
      });
  }, [props.rssfeed]);

  useEffect(() => {
    if (isPlaying) {
      audioElement.current.play();
    } else {
      audioElement.current.pause();
    }
  });

  const SkipControl = (forwards = true) => {
    if (forwards) {
      props.setCurrentSongIndex(() => {
        let temp = props.currentSongIndex;
        temp++;

        if (temp > props.songs.length - 1) {
          temp = 0;
        }
        return temp;
      });
    } else {
      props.setCurrentSongIndex(() => {
        let temp = props.currentSongIndex;
        temp--;

        if (temp < 0) {
          temp = props.songs.length - 1;
        }
        return temp;
      });
    }
  };
  console.log(playerState);

  let playerDisplay = playerState.map((e) => (
    <div>
      {e.title}
      <br />
      {e.sourceAudio}
      <br />
      {JSON.stringify(e.pubDate)}
      <br />
      {e.episodeImage}
      <br />
      <br />
    </div>
  ));

  return (
    <div>
      podcastPlayer
      <br />
      <strong>Previous Podcast:</strong>
      <img
        src={playerState[props.nextSongIndex].episodeImage}
        style={{ width: "4em", height: "auto" }}
      />
    </div>
  );
}

export default PodcastPlayer;
