import "./App.css";
import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudoControls";

function AudioPlayer(episodes) {
  const [episodeIndex, setEpisodeIndex] = useState(0);
  const [episodeProgress, setEpisodeProgress] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const { title, author, episodeImage, sourceAudio } =
    episodes.episodes[episodeIndex];

  const audioRef = useRef(new Audio(sourceAudio));
  const intervalRef = useRef();
  const isReady = useRef(false);

  const { duration } = audioRef.current;

  const toNextEpisode = () => {
    if (episodeIndex - 1 < 0) {
      setEpisodeIndex(episodes.length - 1);
    } else {
      setEpisodeIndex(episodeIndex - 1);
    }
  };

  const toPrevEpisode = () => {
    if (episodeIndex < episodes.length - 1) {
      setEpisodeIndex(episodeIndex + 1);
    } else {
      setEpisodeIndex(0);
    }
  };

  const startTimer = () => {
    clearInterval(intervalRef.current);

    intervalRef.current = setInterval(() => {
      if (audioRef.current.ended) {
        toNextEpisode();
      } else {
        setEpisodeProgress(audioRef.current.currentTime);
      }
    }, [1000]);
  };

  const onScrub = (value) => {
    clearInterval(intervalRef.current);
    audioRef.current.currentTime = value;
    setEpisodeProgress(audioRef.current.currentTime);
  };

  const onScrubEnd = () => {
    if (!isPlaying) {
      setIsPlaying(true);
    }

    startTimer();
  };

  useEffect(() => {
    if (isPlaying) {
      audioRef.current.play();
      startTimer();
    } else {
      audioRef.current.pause();
    }
  }, [isPlaying]);

  useEffect(() => {
    return () => {
      audioRef.current.pause();
      clearInterval(intervalRef.current);
    };
  }, []);

  useEffect(() => {
    audioRef.current.pause();

    audioRef.current = new Audio(sourceAudio);
    setEpisodeIndex(audioRef.current.currentTime);

    if (isReady.current) {
      audioRef.current.play();
      setIsPlaying(true);
      startTimer();
    } else {
      isReady.current = true;
    }
  }, [episodeIndex]);

  return (
    <div className="podcast-player">
      <div className="episode-info">
        <img
          className="episode-poster"
          src={episodeImage}
          alt={`Poster for ${title} episode by ${author}`}
        />
        <h2 className="episode-title">{title}</h2>
        <h2 className="author-text">{author}</h2>
        <input
          type="range"
          value={episodeProgress}
          step="1"
          min="0"
          max={duration ? duration : `${duration}`}
          className="progress"
          onChange={(e) => onScrub(e.target.value)}
          onMouseUp={onScrubEnd}
          onKeyUp={onScrubEnd}
        />
        <AudioControls
          isPlaying={isPlaying}
          onPrevClick={toPrevEpisode}
          onNextClick={toNextEpisode}
          onPlayPauseClick={setIsPlaying}
        />
      </div>
    </div>
  );
}

export default AudioPlayer;
