import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import React, { useState } from "react";
import PodcastGrid from "./PodcastGrid";
import PodcastPlayer from "./PodcastPlayer";

function App() {
  const [rssFeed, setRssFeed] = useState(
    "https://anchor.fm/s/2fa50a94/podcast/rss"
  );
  const [rssList, setRssList] = useState([
    { name: "LTT", url: "https://anchor.fm/s/3cbbb3b8/podcast/rss" },
    { name: "Valuetainment", url: "https://anchor.fm/s/2714310c/podcast/rss" },
    { name: "PBD Podcast", url: "https://anchor.fm/s/2fa50a94/podcast/rss" },
  ]);
  console.log(rssList);

  const handleLoadFeedClick = (inputRssFeed) => {
    setRssFeed(inputRssFeed);
  };

  const RssListButtons = (rssList) => {
    const rssButtonElements = rssList.map(({ name, url }) => {
      <>
        <button
          value={url}
          onClick={(e) => handleLoadFeedClick(e.target.value)}
        >
          {name}
        </button>
      </>;
    });
    return rssButtonElements;
  };

  return (
    <div className="App">
      <div>
        <label htmlFor="rssFeedUrl">RSS Feed URL:</label>
        <input
          type="text"
          id="rssFeedUrl"
          name="rssFeedUrl"
          style={{ width: "80%" }}
          defaultValue={rssFeed}
        />
        <button onClick={(e) => handleLoadFeedClick(e.target.value)}>
          Load Feed
        </button>
        <RssListButtons />
      </div>

      {
        // <PodcastGrid rssfeed={rssFeed} height="50vh" weight="100%"></PodcastGrid>
      }
      <PodcastPlayer rssfeed={rssFeed}></PodcastPlayer>
    </div>
  );
}

export default App;
