import React, { useEffect, useState } from "react";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/dist/styles/ag-grid.css";
import "ag-grid-community/dist/styles/ag-theme-alpine.css";

function PodcastGrid(props) {
  const [rowData, setRowData] = useState([]);
  const [channelTitle, setChannelTitle] = useState("");

  useEffect(() => {
    fetch(props.rssfeed)
      .then((response) => response.text())
      .then((str) => new window.DOMParser().parseFromString(str, "text/xml"))
      .then((data) => {
        const itemList = data.querySelectorAll("item");
        let html = ``;
        const items = [];
        itemList.forEach((e) => {
          items.push({
            pubDate: new Date(e.querySelector("pubDate").textContent),
            title: e.querySelector("title").textContent,
            mp3: e.querySelector("enclosure").getAttribute("url"),
            description: e.querySelector("description").textContent,
            image: e.childNodes[21].attributes.href.value,
          });
        });
        setRowData(items);
      });
  }, [props.rssfeed]);
  const columnDefs = [
    {
      headerName: "Image",
      field: "image",
      width: 100,
      cellRenderer: (params) =>
        `<img style="height: 14px; width: 14px" src=${params.image} />`,
    },
    {
      headerName: "Episode Title",
      field: "title",
      wrapText: true,
      autoHeight: true,
      flex: 3,
      resizable: false,
    },
    {
      headerName: "Published",
      field: "pubDate",
      sortable: true,
    },
    {
      headerName: "Episode",
      field: "mp3",
      autoHeight: true,
      flex: 3,
      cellRenderer: (params) => `<audio controls preload="none">
      <source src=${params.value} type="audio/mpeg" />
    </audio>`,
    },
    {
      headerName: "Description",
      field: "description",
      autoHeight: true,
      flex: 3,
    },
  ];
  console.log(columnDefs);
  return (
    <div
      className="ag-theme-alpine"
      style={{ height: props.height, width: props.width }}
    >
      <h1>{channelTitle}</h1>
      <AgGridReact rowData={rowData} columnDefs={columnDefs}></AgGridReact>
      <img style={{ height: "14px", width: "14px" }} src={rowData[1].image} />
    </div>
  );
}

export default PodcastGrid;
