import "./App.css";
import React, { useState } from "react";
import PodcastGrid from "./PodcastGrid";
import PodcastPlayer from "./PodcastPlayer";


function App() {
  const [rssFeed, setRssFeed] = useState(
    "https://anchor.fm/s/2fa50a94/podcast/rss"
  );

  const handleLoadFeedClick = (inputRssFeed) => {
    setRssFeed(inputRssFeed);
  };
  return (
    <div className="App">
      <div style={{ marginTop: "3em" }}>
        <label htmlFor="rssFeedUrl">RSS Feed URL:</label>
        <input
          type="text"
          id="rssFeedUrl"
          name="rssFeedUrl"
          style={{ width: "80%" }}
          defaultValue={rssFeed}
        />
        <button onClick={(e) => handleLoadFeedClick(e.target.value)}>
          Load Feed
        </button>
        <button
          value={"https://anchor.fm/s/3cbbb3b8/podcast/rss"}
          onClick={(e) => handleLoadFeedClick(e.target.value)}
        >
          LTT
        </button>
        <button
          value={"https://anchor.fm/s/2714310c/podcast/rss"}
          onClick={(e) => handleLoadFeedClick(e.target.value)}
        >
          Valuetainment
        </button>
        <button
          value={"https://anchor.fm/s/2fa50a94/podcast/rss"}
          onClick={(e) => handleLoadFeedClick(e.target.value)}
        >
          PBD Podcast
        </button>
      </div>

      {
        // <PodcastGrid rssfeed={rssFeed} height="50vh" weight="100%"></PodcastGrid>
      }
      <PodcastPlayer rssfeed={rssFeed}></PodcastPlayer>
    </div>
  );
}

export default App;
