import "./App.css";
import React, { useState } from "react";
import PodcastGrid from "./PodcastGrid";
import PodcastPlayer from "./PodcastPlayer";

function App() {
  const [rssFeed, setRssFeed] = useState(
    "https://anchor.fm/s/2fa50a94/podcast/rss"
  );

  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [nextSongIndex, setNextSongIndex] = useState(currentSongIndex + 1);

  const handleLoadFeedClick = () => {
    const inputRssFeed = document.getElementById("rssFeedUrl").value;
    setRssFeed(inputRssFeed);
  };
  return (
    <div className="App">
      <div style={{ marginTop: "3em" }}>
        <label htmlFor="rssFeedUrl">RSS Feed URL:</label>
        <input
          type="text"
          id="rssFeedUrl"
          name="rssFeedUrl"
          style={{ width: "80%" }}
          defaultValue={rssFeed}
        />
        <button onClick={handleLoadFeedClick}>Load Feed</button>
      </div>

      {
        // <PodcastGrid rssfeed={rssFeed} height="50vh" weight="100%"></PodcastGrid>
      }
      <PodcastPlayer
        rssfeed={rssFeed}
        currentSongIndex={currentSongIndex}
        setCurrentSongIndex={setCurrentSongIndex}
        nextSongIndex={nextSongIndex}
        setNextSongIndex={setNextSongIndex}
      ></PodcastPlayer>
    </div>
  );
}

export default App;
