import React from "react";
import {
  BsPlayCircleFill as Play,
  BsFillPauseCircleFill as Pause,
} from "react-icons/bs";

import { BiSkipNext as Next, BiSkipPrevious as Prev } from "react-icons/bi";

const AudioControls = ({
  isPlaying,
  onPlayPauseClick,
  onPrevClick,
  onNextClick,
}) => (
  <div className="audio-controls">
    <button type="button" aria-label="Previous" onClick={onPrevClick}>
      <Prev />
    </button>
    {isPlaying ? (
      <button
        type="button"
        className="pause"
        onClick={() => onPlayPauseClick(false)}
        aria-label="Pause"
      >
        <Pause />
      </button>
    ) : (
      <button
        type="button"
        className="play"
        onClick={() => onPlayPauseClick(true)}
        aria-label="Play"
      >
        <Play />
      </button>
    )}
    <button
      type="button"
      className="next"
      aria-label="Next"
      onClick={onNextClick}
    >
      <Next />
    </button>
  </div>
);
export default AudioControls;
